CREATE PROCEDURE [dbo].[sp_RPT_DATA_KU_KunstlisteDetail_QR]
	 @in_mandant varchar(3) 
	,@in_sprache varchar(2) 
	,@proc varchar(50) -- eigentlich 32, aber auch username
	,@in_standort varchar(36) 
	,@in_kunstler varchar(36) 
	,@in_kunstkategorie varchar(36) 
	,@in_stichtag varchar(50) 
AS 
BEGIN 
	DECLARE @stichtag datetime 
	
	-- Abrunden des Eingabedatums auf 00:00:00 Uhr
	SET @stichtag = CONVERT( DATETIME, @in_stichtag) 
	SET @stichtag = CAST(FLOOR(CAST(@stichtag As Float)) As DateTime)
	SET @in_stichtag = CONVERT(varchar(50), @stichtag) 
	SET @proc = LTRIM(RTRIM(@proc))
	
	
	DECLARE @basiclink varchar(500)
	SET @basiclink = (SELECT TOP 1 FC_Value FROM T_FMS_Configuration WHERE FC_Key = 'basicLink') + '/' 
	-- Vertrauen ist gut, Misstrauen ist besser 
	SET @basiclink = REPLACE(@basiclink, '//', '/') 
	SET @basiclink = REPLACE(@basiclink, 'http:/', 'http://') 
	SET @basiclink = REPLACE(@basiclink, 'https:/', 'https://') 
	
	
	
	DECLARE @unix_timestamp varchar(20)
	-- Greatings reader in 20380120 
	-- http://stackoverflow.com/questions/8837225/unix-timestamp-in-sql-server?nah=1#28743229 
	-- SET @unix_timestamp = CAST({fn timestampdiff(SQL_TSI_SECOND,{d '1970-01-01'}, CURRENT_TIMESTAMP)} AS varchar(20)) 
	
	IF CURRENT_TIMESTAMP >= {d '2038-01-19'}  
	BEGIN 
		SET @unix_timestamp = CAST
		(
			CAST
			(
				{fn timestampdiff(SQL_TSI_SECOND,{d '1970-01-01'}, {d '2038-01-19'})} 
				AS bigint
			)  
			+ 
			CAST
			(
				{fn timestampdiff(SQL_TSI_SECOND,{d '2038-01-19'}, CURRENT_TIMESTAMP)}
				AS bigint
			) 
		AS varchar(20)
		) 
	END 
	ELSE 
		SET @unix_timestamp = CAST({fn timestampdiff(SQL_TSI_SECOND,{d '1970-01-01'}, CURRENT_TIMESTAMP)} AS varchar(20))
		
	-- PRINT @unix_timestamp 
	
	SET NOCOUNT ON;
	
	

	-- Debugging: 
	-- SET @basiclink = 'https://www6.cor-asp.ch/COR_Basic_ArtGallery/'
	-- SET @basiclink = 'https://www6.cor-asp.ch/COR_Basic_Demoabc/' 
	-- SET @unix_timestamp = '1453898183'
	
	
	--SELECT @basiclink, @unix_timestamp
	
	;
	WITH CTE AS 
	( 
		SELECT 
			 0 AS lvl 
			,KU_UID AS MainUID 
			,KU_RM_UID AS MainRM_UID 
			,* 
		FROM T_AP_Kunst 
		WHERE KU_KU_UID IS NULL 
		
		UNION ALL 
		
		SELECT 
			 CTE.lvl + 1 AS lvl 
			,CTE.MainUID AS MainUID 
			,CTE.MainRM_UID AS MainRM_UID 
			,T_AP_Kunst.* 
		FROM CTE 
		
		INNER JOIN  T_AP_Kunst 
			ON T_AP_Kunst.KU_KU_UID = CTE.KU_UID 
	) 
	SELECT 
		  CTE.KU_UID AS RPT_UID 
		  
		 ,GB_Nr + '-' + GST_Kurz_DE + RIGHT('00' + GS_Nr, 2) + '-' + RM_Nr AS RPT_Lage 
		 
		,'Autorenschaft: ' + 
		STUFF 
		( 
			(
				SELECT 
					'/ ' + 
					ISNULL(KK_Lang_DE, '') + ISNULL(', ' + KK_Kurz_DE, '') 
					+ ISNULL
					(
						' (* ' 
						+ CAST(T_AP_Ref_KunstKuenstler.KK_Geburtsjahr AS varchar(4)) 
						+ ISNULL('   -   † ' + CAST(KK_Todesjahr AS varchar(4)), '')
						+ ') ' 
						,''
					) AS [text()]  
				FROM T_ZO_AP_Kunst_AP_Ref_KunstKuenstler 
					
					
				LEFT JOIN T_AP_Ref_KunstKuenstler
					ON T_AP_Ref_KunstKuenstler.KK_UID = T_ZO_AP_Kunst_AP_Ref_KunstKuenstler.ZO_KUKK_KK_UID
					AND T_AP_Ref_KunstKuenstler.KK_Status = 1 	
					
				WHERE T_ZO_AP_Kunst_AP_Ref_KunstKuenstler.ZO_KUKK_Status = 1 
				AND T_ZO_AP_Kunst_AP_Ref_KunstKuenstler.ZO_KUKK_KU_UID = CTE.KU_UID 
				ORDER BY KK_Lang_DE, KK_Kurz_DE 
				
				FOR XML PATH, TYPE
			).value('.[1]', 'nvarchar(MAX)')  
			, 1
			, 1
			, '' 
		) AS RPT_Name
		
		,'Titel: ' + CTE.KU_Bezeichnung AS RPT_Title 
		
		,'Entstehungszeit: ' 
		+ ISNULL(CAST(CTE.KU_EnstehungszeitVon AS varchar(4)), '') 
		+ 
		ISNULL(' - ' + CAST(CTE.KU_EnstehungszeitBis AS varchar(4)), '') 
		AS RPT_Entstehungszeit 
		
		,KKAT_Lang_DE + ', ' + CAST(CTE.KU_Bemerkung AS varchar(7000)) AS RPT_Beschreibung 
		
		
		,CTE.KU_Masse_Hoehe
		,CTE.KU_Masse_Breite
		,CTE.KU_Masse_Tiefe
		
		
		,CTE.KU_Masse_Hoehe_Rahmen
		,CTE.KU_Masse_Breite_Rahmen
		,CTE.KU_Masse_Tiefe_Rahmen
		
		,
		@basiclink 
			+ 'Forms/Room.aspx?no_cache=' + @unix_timestamp 
			+ '&uid=' + CAST(MainRM_UID AS varchar(36)) 
			+ '&env=ov&ro=false'
			+ '&proc=' + @proc 
			+ '&mandant=' + @in_mandant 
		AS RPT_Link_Raum 
		
		,
		@basiclink 
			+ 'Forms/Art.aspx?no_cache=' + @unix_timestamp
			+ '&uid=' + CAST(KU_UID AS varchar(36)) 
			+ '&env=ov&ro=false'
			+ '&proc=' + @proc
			+ '&mandant=' + @in_mandant 
		AS RPT_Link_Form 
		
		--,=Code.GenerateQrCode(Fields!RPT_QrCodeText.Value)
		,
		dbo.fu_CLR_GenerateQR
		(
			@basiclink 
			+ N'kunst_visualize.aspx?no_cache=' + @unix_timestamp 
			+ N'&uid=' + CAST(KU_UID AS nvarchar(36)) 
			-- + N'&env=ov&ro=false'
			-- + N'&proc=' + @proc
			-- + N'&mandant=' + @in_mandant 
			-- Toll, kann einfach jeder sehen 
		)
		AS RPT_QrCodeText 
		
		,
		@basiclink 
			+ 'ajax/File.ashx?no_cache=' + @unix_timestamp
			+ '&DK_UID=' + CAST(ISNULL(T_AP_Dokumente.DK_UID, '00000000-0000-0000-0000-000000000000') AS varchar(36)) 
			-- Hier auch keine Authentifizierung...
		AS RPT_ImageLink 
		
		,
		-- KU_Exemplar ||/XOR KU_IsAuflage ? 
		CASE WHEN CTE.KU_Exemplar = 'true' 
			THEN 'Einzelwerk' 
			ELSE '' 
		END AS RPT_Einzelwerk 
		
		,ISNULL('SNB-Nr. '  + CTE.KU_Nr, '') AS RPT_SNB_Nr 
		
		,NULL AS RPT_QrCodeMime 
		,NULL AS RPT_QrCode 
		
		,
		CASE 
			WHEN LEFT(T_RuediIstEinGottVerdammterVollidiotWorkaround.MIME_Type, 5) = 'image' AND T_AP_Dokumente.DK_File IS NOT NULL THEN 
				ISNULL(T_RuediIstEinGottVerdammterVollidiotWorkaround.MIME_Type, 'image/jpeg') 
			ELSE 'image/jpeg' 
		END AS RPT_Mime
		
		
		,
		CASE 
			WHEN LEFT(T_RuediIstEinGottVerdammterVollidiotWorkaround.MIME_Type, 5) = 'image' AND T_AP_Dokumente.DK_File IS NOT NULL THEN 
				T_AP_Dokumente.DK_File   
		ELSE 
			CAST
			(
				-- Default-Bild 
				
				AS varbinary(MAX)
			) 
		END AS RPT_Thumbnail
		
	FROM CTE -- T_AP_Kunst AS 

	LEFT JOIN T_AP_Dokumente 
		ON T_AP_Dokumente.DK_Objekt_UID = CTE.KU_UID -- Ruedi-FU 
		AND T_AP_Dokumente.DK_Status = 1 
		AND T_AP_Dokumente.DK_FileUploaded = 1 
		AND T_AP_Dokumente.DK_IsDefault = 1 
		
	LEFT JOIN T_SYS_Ref_MimeTypes AS T_RuediIstEinGottVerdammterVollidiotWorkaround 
		ON 
		( 
			T_RuediIstEinGottVerdammterVollidiotWorkaround.MIME_FileExtension = T_AP_Dokumente.DK_Dateiformat 
			OR 
			T_RuediIstEinGottVerdammterVollidiotWorkaround.MIME_FileExtensionNoPoint = T_AP_Dokumente.DK_Dateiformat 
		) 
		
	LEFT JOIN T_AP_Ref_KunstKategorie -- Gattung 
		ON T_AP_Ref_KunstKategorie.KKAT_UID =  CTE.KU_KKAT_UID 
		AND T_AP_Ref_KunstKategorie.KKAT_Status = 1 
		
	LEFT JOIN T_AP_Ref_KunstStatus
		ON T_AP_Ref_KunstStatus.KS_UID = CTE.KU_KS_UID 
		AND T_AP_Ref_KunstStatus.KS_Status = 1 
		
	LEFT JOIN T_AP_Raum 
		ON T_AP_Raum.RM_UID = CTE.MainRM_UID 
		AND T_AP_Raum.RM_Status = 1 
		
	LEFT JOIN T_AP_Geschoss 
		ON T_AP_Geschoss.GS_UID = T_AP_Raum.RM_GS_UID 
		AND T_AP_Geschoss.GS_Status = 1 
		
	LEFT JOIN T_AP_Ref_Geschosstyp 
		ON T_AP_Ref_Geschosstyp.GST_UID = T_AP_Geschoss.GS_GST_UID 
		AND T_AP_Ref_Geschosstyp.GST_Status = 1 
		
	LEFT JOIN T_AP_Gebaeude 
		ON T_AP_Gebaeude.GB_UID = T_AP_Geschoss.GS_GB_UID 
		AND T_AP_Gebaeude.GB_Status = 1 
		
	LEFT JOIN T_AP_Standort 
		ON T_AP_Standort.SO_UID = T_AP_Gebaeude.GB_SO_UID 
		AND T_AP_Standort.SO_Status = 1 
		
	WHERE (1=1) 
	AND CTE.KU_Status = 1 
	AND @stichtag BETWEEN CONVERT(char(8), CTE.KU_DatumVon, 112) AND CTE.KU_DatumBis 
	
	AND 
	(
		T_AP_Standort.SO_UID = @in_standort 
		OR 
		@in_standort = '00000000-0000-0000-0000-000000000000'
	)
	
	AND 
	(
		T_AP_Ref_KunstKategorie.KKAT_UID = @in_kunstkategorie 
		OR 
		@in_kunstkategorie = '00000000-0000-0000-0000-000000000000'
	)
	
	AND
	(
		@in_kunstler = '00000000-0000-0000-0000-000000000000'
		OR 
		CTE.KU_UID IN
		(
			SELECT ZO_KUKK_KU_UID FROM T_ZO_AP_Kunst_AP_Ref_KunstKuenstler 
			WHERE ZO_KUKK_KU_UID = CTE.KU_UID
			AND ZO_KUKK_KK_UID = '15892352-DEF4-432A-AECD-B3484C6F7532' 
		)
	) 

	AND CTE.KU_UID NOT IN 
	( 
		SELECT ISNULL(KU_UID, '00000000-0000-0000-0000-000000000000') 
		FROM T_AP_Kunst WHERE KU_KU_UID IS NULL 
		AND KU_UID IN 
		( 
			SELECT ISNULL(t2.KU_KU_UID, '00000000-0000-0000-0000-000000000000') FROM T_AP_Kunst as t2 
			WHERE t2.KU_KU_UID = T_AP_Kunst.KU_UID 
		) 
	) 
	
	
	ORDER BY MainUID, lvl 
END 


